import * as THREE from 'three';

export function createBasicTrainCabin() {
    const cabin = new THREE.Group();

    // Main cabin body
    const body = new THREE.Mesh(
        new THREE.BoxGeometry(4, 3, 5),
        new THREE.MeshStandardMaterial({
            color: 0x2c3e50,
            metalness: 0.8,
            roughness: 0.2
        })
    );
    body.position.y = 1.5;
    cabin.add(body);

    // Front window
    const frontWindow = new THREE.Mesh(
        new THREE.PlaneGeometry(3, 2),
        new THREE.MeshPhongMaterial({ 
            color: 0x84c1ff,
            transparent: true,
            opacity: 0.6
        })
    );
    frontWindow.position.set(0, 2, 2.51);
    cabin.add(frontWindow);

    // Control console
    const console = new THREE.Mesh(
        new THREE.BoxGeometry(3, 1, 1),
        new THREE.MeshPhongMaterial({ color: 0x34495e })
    );
    console.position.set(0, 1.2, 2);
    cabin.add(console);

    // Throttle lever
    const lever = new THREE.Mesh(
        new THREE.BoxGeometry(0.1, 0.4, 0.1),
        new THREE.MeshPhongMaterial({ color: 0xe74c3c })
    );
    lever.position.set(0.5, 1.7, 2);
    lever.name = "throttle_lever";
    cabin.add(lever);

    // Add dashboard displays
    const displays = new THREE.Mesh(
        new THREE.BoxGeometry(2, 0.5, 0.1),
        new THREE.MeshPhongMaterial({ 
            color: 0x2ecc71,
            emissive: 0x27ae60,
            emissiveIntensity: 0.5
        })
    );
    displays.position.set(0, 1.5, 2.1);
    cabin.add(displays);

    return cabin;
}
