import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store';
import { WebSocketService } from '../services/WebSocketService';
import '../styles/TrainCabin.css';

const TrainCabin: React.FC = () => {
    const dispatch = useDispatch();
    const { engineStatus, throttlePosition, speed } = useSelector((state: RootState) => state.simulation);
    const wsRef = React.useRef<WebSocketService>();

    useEffect(() => {
        wsRef.current = new WebSocketService(dispatch);
        wsRef.current.connect();

        const handleControls = (event: KeyboardEvent) => {
            switch(event.key) {
                case 'e':
                    wsRef.current?.sendUpdate({ 
                        engine_status: engineStatus === 'off' ? 'on' : 'off',
                        throttle_position: engineStatus === 'off' ? 0 : throttlePosition 
                    });
                    break;
                case 'ArrowUp':
                    if (engineStatus === 'on') {
                        wsRef.current?.sendUpdate({ 
                            throttle_position: Math.min(100, throttlePosition + 10) 
                        });
                    }
                    break;
                case 'ArrowDown':
                    if (engineStatus === 'on') {
                        wsRef.current?.sendUpdate({ 
                            throttle_position: Math.max(0, throttlePosition - 10) 
                        });
                    }
                    break;
            }
        };

        window.addEventListener('keydown', handleControls);
        return () => window.removeEventListener('keydown', handleControls);
    }, [dispatch, engineStatus, throttlePosition]);

    return (
        <div className="train-cabin">
            <div className="dashboard">
                <div className={`engine-status ${engineStatus}`}>
                    Engine: {engineStatus.toUpperCase()}
                </div>
                <div className="speedometer">
                    <div className="speed-value">{speed.toFixed(1)} km/h</div>
                    <div className="speed-gauge">
                        <div 
                            className="speed-fill"
                            style={{ width: `${(speed / 120) * 100}%` }}
                        />
                    </div>
                </div>
                <div className="throttle-gauge">
                    <label>Throttle</label>
                    <div className="gauge-bar">
                        <div 
                            className="gauge-fill"
                            style={{ width: `${throttlePosition}%` }}
                        />
                    </div>
                </div>
            </div>
            <div className="controls-guide">
                <h3>Controls:</h3>
                <ul>
                    <li>E - Start/Stop Engine</li>
                    <li>↑ - Increase Throttle</li>
                    <li>↓ - Decrease Throttle</li>
                </ul>
            </div>
        </div>
    );
};

export default TrainCabin;
