import React, { useEffect, useRef, useState } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store';
import { WebSocketService } from '../services/WebSocketService';
import { Sky } from 'three/examples/jsm/objects/Sky';
import { soundService } from '../services/SoundService';
import WeatherEffects from './WeatherEffects';
import { Water } from 'three/examples/jsm/objects/Water';

const TrainCabin: React.FC = () => {
    const containerRef = useRef<HTMLDivElement>(null);
    const throttleRef = useRef<THREE.Object3D>();
    const wsRef = useRef<WebSocketService>();
    const dispatch = useDispatch();
    const { engineStatus, throttlePosition, speed } = useSelector((state: RootState) => state.simulation);
    const [sceneRef, setSceneRef] = useState<THREE.Scene | null>(null);
    const [timeOfDay, setTimeOfDay] = useState(12); // 24-hour format
    const [weather, setWeather] = useState<'clear' | 'rain' | 'fog'>('clear');
    const sunLightRef = useRef<THREE.DirectionalLight>();

    useEffect(() => {
        wsRef.current = new WebSocketService(dispatch);
        wsRef.current.connect();
    }, [dispatch]);

    useEffect(() => {
        if (!containerRef.current) return;
        const scene = new THREE.Scene();
        setSceneRef(scene);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.shadowMap.enabled = true;
        containerRef.current.appendChild(renderer.domElement);

        // Add lights
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
        directionalLight.position.set(5, 5, 5);
        directionalLight.castShadow = true;
        scene.add(directionalLight);

        // Add controls
        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Load train cabin model
        const loader = new GLTFLoader();
        loader.load("/assets/train_cabin.glb", (gltf) => {
            scene.add(gltf.scene);
            // Find throttle lever in the model
            throttleRef.current = gltf.scene.getObjectByName("throttle_lever");
        });

        // Add control panel
        const controlPanel = new THREE.Mesh(
            new THREE.BoxGeometry(2, 1, 0.1),
            new THREE.MeshPhongMaterial({ color: 0x333333 })
        );
        controlPanel.position.set(0, -1, -2);
        scene.add(controlPanel);

        camera.position.set(0, 1.6, 0); // Eye level

        // Add sky
        const sky = new Sky();
        sky.scale.setScalar(450000);
        scene.add(sky);

        // Add dynamic lighting
        const sunLight = new THREE.DirectionalLight(0xffffff, 1);
        sunLight.position.set(0, 1, 0);
        sunLight.castShadow = true;
        scene.add(sunLight);
        sunLightRef.current = sunLight;

        // Add environment fog
        scene.fog = new THREE.Fog(0x87ceeb, 1, 1000);

        // Add terrain
        const terrainGeometry = new THREE.PlaneGeometry(1000, 1000, 100, 100);
        const terrainMaterial = new THREE.MeshStandardMaterial({ 
            color: 0x355E3B,
            roughness: 0.8,
            metalness: 0.2
        });
        const terrain = new THREE.Mesh(terrainGeometry, terrainMaterial);
        terrain.rotation.x = -Math.PI / 2;
        terrain.position.y = -10;
        terrain.receiveShadow = true;
        scene.add(terrain);

        // Add railway tracks
        const trackGeometry = new THREE.BoxGeometry(2, 0.2, 1000);
        const trackMaterial = new THREE.MeshStandardMaterial({ color: 0x4a4a4a });
        const track = new THREE.Mesh(trackGeometry, trackMaterial);
        track.position.y = -5;
        track.receiveShadow = true;
        scene.add(track);

        // Update throttle controls
        const handleControls = (event: KeyboardEvent) => {
            if (!throttleRef.current) return;

            switch(event.key) {
                case 'e':
                    const newEngineStatus = engineStatus === 'off' ? 'on' : 'off';
                    wsRef.current?.sendUpdate({
                        engine_status: newEngineStatus
                    });
                    if (newEngineStatus === 'on') {
                        soundService.startEngine();
                    } else {
                        soundService.stopEngine();
                    }
                    break;
                case 'ArrowUp':
                    const newThrottle = Math.min(100, throttlePosition + 5);
                    wsRef.current?.sendUpdate({
                        throttle_position: newThrottle
                    });
                    soundService.updateThrottle(newThrottle);
                    break;
                case 'ArrowDown':
                    wsRef.current?.sendUpdate({
                        throttle_position: Math.max(0, throttlePosition - 5)
                    });
                    soundService.applyBrake();
                    break;
                case 't':
                    setTimeOfDay((prev) => (prev + 1) % 24);
                    break;
                case 'w':
                    setWeather((prev) => {
                        const states: ('clear' | 'rain' | 'fog')[] = ['clear', 'rain', 'fog'];
                        const currentIndex = states.indexOf(prev);
                        return states[(currentIndex + 1) % states.length];
                    });
                    break;
            }
        };

        window.addEventListener('keydown', handleControls);

        const animate = () => {
            requestAnimationFrame(animate);

            // Add camera shake based on speed
            if (speed > 0) {
                const shakeIntensity = speed / 1000;
                camera.position.y = 1.6 + Math.sin(Date.now() * 0.01) * shakeIntensity;
                camera.position.x = Math.cos(Date.now() * 0.01) * shakeIntensity;
            }

            controls.update();
            renderer.render(scene, camera);
        };

        animate();

        // Cleanup
        return () => {
            window.removeEventListener('keydown', handleControls);
            renderer.dispose();
        };
    }, [engineStatus, throttlePosition, speed]);

    useEffect(() => {
        if (!sceneRef) return;

        if (weather === 'fog') {
            sceneRef.fog = new THREE.FogExp2(0xcccccc, 0.02);
        } else {
            sceneRef.fog = null;
        }
    }, [weather, sceneRef]);

    // Add time of day controls
    useEffect(() => {
        if (!sceneRef || !sunLightRef.current) return;

        const sunAngle = (timeOfDay - 12) * (Math.PI / 12);
        sunLightRef.current.position.x = Math.cos(sunAngle) * 100;
        sunLightRef.current.position.y = Math.sin(sunAngle) * 100;

        // Update sky color based on time
        const nightness = 1 - Math.sin((timeOfDay / 24) * Math.PI);
        sceneRef.background = new THREE.Color(
            0.5 - nightness * 0.5,
            0.7 - nightness * 0.7,
            1.0 - nightness * 0.5
        );

        // Update light intensity
        sunLightRef.current.intensity = Math.max(0.2, Math.sin((timeOfDay / 24) * Math.PI));
    }, [timeOfDay, sceneRef]);

    // Add water effect
    useEffect(() => {
        if (!sceneRef) return;

        const waterGeometry = new THREE.PlaneGeometry(10000, 10000);
        const water = new Water(waterGeometry, {
            textureWidth: 512,
            textureHeight: 512,
            waterNormals: new THREE.TextureLoader().load('assets/waternormals.jpg', (texture) => {
                texture.wrapS = texture.wrapT = THREE.RepeatWrapping;
            }),
            sunDirection: new THREE.Vector3(),
            sunColor: 0xffffff,
            waterColor: 0x001e0f,
            distortionScale: 3.7,
            fog: sceneRef.fog !== undefined
        });
        water.rotation.x = -Math.PI / 2;
        water.position.y = -15;
        sceneRef.add(water);
    }, [sceneRef]);

    return (
        <>
            <div ref={containerRef} style={{ width: '100vw', height: '100vh' }} />
            {sceneRef && weather === 'rain' && <WeatherEffects scene={sceneRef} />}
        </>
    );
};

export default TrainCabin;
