import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store';
import { WebSocketService } from '../services/WebSocketService';
import '../styles/TrainCabin.css';

const TrainCabin: React.FC = () => {
    const dispatch = useDispatch();
    const { engineStatus, throttlePosition, speed } = useSelector((state: RootState) => {
        console.log('Current state:', state.simulation);
        return state.simulation;
    });
    const wsRef = React.useRef<WebSocketService>();

    useEffect(() => {
        console.log('Initializing WebSocket...');
        wsRef.current = new WebSocketService(dispatch);
        wsRef.current.connect();
    }, [dispatch]);

    const handleEngineToggle = () => {
        const newStatus = engineStatus === 'off' ? 'on' : 'off';
        console.log('Toggling engine to:', newStatus);
        wsRef.current?.sendUpdate({ 
            engine_status: newStatus,
            throttle_position: newStatus === 'off' ? 0 : throttlePosition 
        });
    };

    const handleThrottleIncrease = () => {
        if (engineStatus === 'on') {
            const newThrottle = Math.min(100, throttlePosition + 10);
            console.log('Increasing throttle to:', newThrottle);
            wsRef.current?.sendUpdate({ throttle_position: newThrottle });
        }
    };

    const handleThrottleDecrease = () => {
        if (engineStatus === 'on') {
            const reducedThrottle = Math.max(0, throttlePosition - 10);
            console.log('Decreasing throttle to:', reducedThrottle);
            wsRef.current?.sendUpdate({ throttle_position: reducedThrottle });
        }
    };

    return (
        <div className="train-cabin">
            <div className="dashboard">
                <div className={`engine-status ${engineStatus}`}>
                    Engine: {engineStatus.toUpperCase()}
                </div>
                <button 
                    className={`engine-button ${engineStatus}`}
                    onClick={handleEngineToggle}
                >
                    {engineStatus === 'off' ? 'Start Engine' : 'Stop Engine'}
                </button>

                <div className="speedometer">
                    <div className="speed-value">{speed.toFixed(1)} km/h</div>
                    <div className="speed-gauge">
                        <div 
                            className="speed-fill"
                            style={{ width: `${(speed / 120) * 100}%` }}
                        />
                    </div>
                </div>

                <div className="throttle-controls">
                    <div className="throttle-gauge">
                        <label>Throttle: {throttlePosition}%</label>
                        <div className="gauge-bar">
                            <div 
                                className="gauge-fill"
                                style={{ width: `${throttlePosition}%` }}
                            />
                        </div>
                    </div>
                    <div className="throttle-buttons">
                        <button 
                            onClick={handleThrottleIncrease}
                            disabled={engineStatus === 'off'}
                            className="throttle-button increase"
                        >
                            Increase Throttle
                        </button>
                        <button 
                            onClick={handleThrottleDecrease}
                            disabled={engineStatus === 'off'}
                            className="throttle-button decrease"
                        >
                            Decrease Throttle
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TrainCabin;
