class SoundService {
    private engineSound: HTMLAudioElement;
    private throttleSound: HTMLAudioElement;
    private brakeSound: HTMLAudioElement;

    constructor() {
        this.engineSound = new Audio('/assets/sounds/engine-idle.mp3');
        this.engineSound.loop = true;
        this.throttleSound = new Audio('/assets/sounds/throttle.mp3');
        this.brakeSound = new Audio('/assets/sounds/brake.mp3');
    }

    startEngine() {
        this.engineSound.play();
        this.engineSound.volume = 0.3;
    }

    stopEngine() {
        this.engineSound.pause();
        this.engineSound.currentTime = 0;
    }

    updateThrottle(position: number) {
        this.engineSound.volume = 0.3 + (position / 100) * 0.7;
        this.throttleSound.play();
    }

    applyBrake() {
        this.brakeSound.play();
    }
}

export const soundService = new SoundService();
