import React, { useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store';
import { WebSocketService } from '../services/WebSocketService';
import '../styles/TrainCabin.css';

const TrainCabin: React.FC = () => {
    const dispatch = useDispatch();
    const { engineStatus, throttlePosition, speed, brakePressure, fuelLevel } = 
        useSelector((state: RootState) => state.simulation);
    const wsRef = useRef<WebSocketService>();

    useEffect(() => {
        console.log('Initializing WebSocket service...');
        wsRef.current = new WebSocketService(dispatch);
        wsRef.current.connect();

        return () => {
            if (wsRef.current) {
                wsRef.current.disconnect();
            }
        };
    }, [dispatch]);

    const handleEngineToggle = () => {
        if (!wsRef.current) return;
        const newStatus = engineStatus === 'off' ? 'on' : 'off';
        wsRef.current.sendUpdate({
            engine_status: newStatus,
            throttle_position: newStatus === 'off' ? 0 : throttlePosition
        });
    };

    const handleThrottleIncrease = () => {
        if (engineStatus === 'on' && wsRef.current) {
            const newThrottle = Math.min(100, throttlePosition + 10);
            wsRef.current.sendUpdate({ throttle_position: newThrottle });
        }
    };

    const handleThrottleDecrease = () => {
        if (engineStatus === 'on' && wsRef.current) {
            const reducedThrottle = Math.max(0, throttlePosition - 10);
            wsRef.current.sendUpdate({ throttle_position: reducedThrottle });
        }
    };

    return (
        <div className="train-cabin">
            <div className="cabin-interior">
                <div className="cabin-window">
                    <div className="window-frame" />
                    <div className="window-view" />
                </div>

                <div className="control-desk">
                    <div className="desk-panel left">
                        <div className="gauge-cluster">
                            <div className="speedometer">
                                <div className="speed-dial">
                                    {[...Array(12)].map((_, i) => (
                                        <div 
                                            key={i} 
                                            className="dial-mark"
                                            style={{ 
                                                transform: `rotate(${i * 30}deg)` 
                                            }}
                                        />
                                    ))}
                                    <div 
                                        className="speed-needle"
                                        style={{ '--speed': speed } as any}
                                    />
                                    <div className="speed-center" />
                                    <div className="speed-value">
                                        {speed.toFixed(1)}
                                        <span className="speed-unit">km/h</span>
                                    </div>
                                </div>
                            </div>

                            <div className="multi-gauge">
                                <div className="gauge pressure">
                                    <div className="gauge-label">Brake Pressure</div>
                                    <div className="gauge-dial">
                                        <div 
                                            className="gauge-needle"
                                            style={{ 
                                                transform: `rotate(${(brakePressure / 100) * 270 - 135}deg)` 
                                            }}
                                        />
                                        <div className="gauge-value">{brakePressure}%</div>
                                    </div>
                                </div>
                                <div className="gauge fuel">
                                    <div className="gauge-label">Fuel Level</div>
                                    <div className="gauge-dial">
                                        <div 
                                            className="gauge-needle"
                                            style={{ 
                                                transform: `rotate(${(fuelLevel / 100) * 270 - 135}deg)` 
                                            }}
                                        />
                                        <div className="gauge-value">{fuelLevel}%</div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="desk-panel center">
                        <div className="engine-controls">
                            <div className={`engine-status ${engineStatus}`}>
                                <div className="status-light" />
                                <h2>Engine Status: {engineStatus.toUpperCase()}</h2>
                            </div>

                            <button
                                className={`engine-button ${engineStatus}`}
                                onClick={handleEngineToggle}
                            >
                                {engineStatus === 'off' ? 'Start Engine' : 'Stop Engine'}
                            </button>
                        </div>

                        <div className="throttle-controls">
                            <div className="throttle-housing">
                                <div className="throttle-label">Throttle</div>
                                <div className="throttle-slot">
                                    <div className="throttle-marks">
                                        {[...Array(11)].map((_, i) => (
                                            <div 
                                                key={i} 
                                                className="mark"
                                                style={{ bottom: `${i * 10}%` }}
                                            >
                                                {100 - i * 10}
                                            </div>
                                        ))}
                                    </div>
                                    <div 
                                        className="throttle-handle"
                                        style={{ '--throttle-position': `${100 - throttlePosition}%` } as any}
                                    >
                                        <div className="handle-grip" />
                                    </div>
                                </div>
                            </div>

                            <div className="throttle-buttons">
                                <button
                                    onClick={handleThrottleIncrease}
                                    disabled={engineStatus === 'off'}
                                    className="control-button"
                                >
                                    Increase
                                </button>
                                <button
                                    onClick={handleThrottleDecrease}
                                    disabled={engineStatus === 'off'}
                                    className="control-button"
                                >
                                    Decrease
                                </button>
                            </div>
                        </div>
                    </div>

                    <div className="desk-panel right">
                        <div className="warning-lights">
                            <div className={`warning-light ${engineStatus === 'on' ? 'active' : ''}`}>
                                Engine
                            </div>
                            <div className={`warning-light ${brakePressure < 30 ? 'active' : ''}`}>
                                Brake
                            </div>
                            <div className={`warning-light ${fuelLevel < 20 ? 'active' : ''}`}>
                                Fuel
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TrainCabin;
