import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store';
import { WebSocketService } from '../services/WebSocketService';
import '../styles/TrainCabin.css';

const TrainCabin: React.FC = () => {
    const dispatch = useDispatch();
    const { engineStatus, throttlePosition, speed, brakePressure, fuelLevel, engineTemp, oilPressure, cabinTemp, fanSpeed } = 
        useSelector((state: RootState) => state.simulation);
    const wsRef = useRef<WebSocketService>();
    const [isDragging, setIsDragging] = useState(false);
    const throttleRef = useRef<HTMLDivElement>(null);
    const [trackOffset, setTrackOffset] = useState(0);
    const [prevSpeed, setPrevSpeed] = useState(0);
    const [lightsOn, setLightsOn] = useState(false);
    const [wipersOn, setWipersOn] = useState(false);
    const [hornActive, setHornActive] = useState(false);

    useEffect(() => {
        console.log('Initializing WebSocket service...');
        wsRef.current = new WebSocketService(dispatch);
        wsRef.current.connect();

        return () => {
            if (wsRef.current) {
                wsRef.current.disconnect();
            }
        };
    }, [dispatch]);

    useEffect(() => {
        let animationFrame: number;
        const animateTrack = () => {
            setTrackOffset(prev => (prev + speed * 0.1) % 100);
            animationFrame = requestAnimationFrame(animateTrack);
        };
        animationFrame = requestAnimationFrame(animateTrack);
        return () => cancelAnimationFrame(animationFrame);
    }, [speed]);

    const handleEngineToggle = () => {
        if (!wsRef.current) return;
        const newStatus = engineStatus === 'off' ? 'on' : 'off';
        wsRef.current.sendUpdate({
            engine_status: newStatus,
            throttle_position: newStatus === 'off' ? 0 : throttlePosition
        });
    };

    const handleThrottleMove = useCallback((clientY: number) => {
        if (!throttleRef.current || engineStatus === 'off') return;

        const rect = throttleRef.current.getBoundingClientRect();
        const height = rect.height - 50; // Adjust for handle height
        const relativeY = Math.max(0, Math.min(height, clientY - rect.top));
        const percentage = Math.round(((height - relativeY) / height) * 100);

        wsRef.current?.sendUpdate({ throttle_position: percentage });
    }, [engineStatus]);

    const handleMouseDown = (e: React.MouseEvent) => {
        if (engineStatus === 'off') return;
        setIsDragging(true);
        handleThrottleMove(e.clientY);
    };

    const handleMouseMove = useCallback((e: React.MouseEvent) => {
        if (!isDragging) return;
        handleThrottleMove(e.clientY);
    }, [isDragging, handleThrottleMove]);

    const handleMouseUp = () => {
        setIsDragging(false);
    };

    useEffect(() => {
        if (isDragging) {
            document.addEventListener('mouseup', handleMouseUp);
            document.addEventListener('mousemove', handleMouseMove as any);
        }
        return () => {
            document.removeEventListener('mouseup', handleMouseUp);
            document.removeEventListener('mousemove', handleMouseMove as any);
        };
    }, [isDragging, handleMouseMove, handleMouseUp]);

    const handleBrakeChange = (value: number) => {
        wsRef.current?.sendUpdate({ brake_pressure: value });
    };

    const handleLightsToggle = () => {
        setLightsOn(!lightsOn);
        wsRef.current?.sendUpdate({ lights: !lightsOn });
    };

    const handleWipersToggle = () => {
        setWipersOn(!wipersOn);
        wsRef.current?.sendUpdate({ wipers: !wipersOn });
    };

    const handleHornPress = () => {
        setHornActive(true);
        wsRef.current?.sendUpdate({ horn: true });
        // Auto-release horn after 1 second
        setTimeout(() => {
            setHornActive(false);
            wsRef.current?.sendUpdate({ horn: false });
        }, 1000);
    };

    const handleEmergencyStop = () => {
        wsRef.current?.sendUpdate({
            throttle_position: 0,
            brake_pressure: 100,
            engine_status: 'off'
        });
    };

    const handleTempChange = (direction: 'up' | 'down') => {
        const newTemp = direction === 'up' ? cabinTemp + 1 : cabinTemp - 1;
        wsRef.current?.sendUpdate({ cabin_temp: newTemp });
    };

    const handleFanToggle = () => {
        const newSpeed = fanSpeed === 4 ? 0 : fanSpeed + 1;
        wsRef.current?.sendUpdate({ fan_speed: newSpeed });
    };

    return (
        <div className="train-cabin">
            {/* Enhanced 3D View Section */}
            <div className="view-section">
                <div className="cabin-frame">
                    <div className="window-frame">
                        <div className="window-view">
                            <div className="sky">
                                <div className="stars"></div>
                            </div>
                            <div className="scenery">
                                {[...Array(3)].map((_, i) => (
                                    <div 
                                        key={`mountain-${i}`}
                                        className="mountain"
                                        style={{ 
                                            left: `${i * 30 + 10}%`,
                                            transform: `translateZ(${-800 - i * 100}px)`
                                        }}
                                    >
                                        <div className="mountain-shape">
                                            <div className="mountain-snow"></div>
                                        </div>
                                    </div>
                                ))}
                                {[...Array(10)].map((_, i) => (
                                    <div 
                                        key={`scenery-${i}`}
                                        className="scenery-item"
                                        style={{
                                            left: `${i * 20}%`,
                                            animationDelay: `${i * 2}s`
                                        }}
                                    >
                                        <div className="tree">
                                            <div className="tree-top"></div>
                                            <div className="tree-trunk"></div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                            <div className="track-system">
                                <div className="ground"></div>
                                <div className="tracks">
                                    {[...Array(20)].map((_, i) => (
                                        <div 
                                            key={`track-${i}`}
                                            className="track-segment"
                                            style={{
                                                transform: `translateZ(${i * 50}px)`,
                                                animationDelay: `${i * 0.15}s`
                                            }}
                                        >
                                            <div className="rail left"></div>
                                            <div className="rail right"></div>
                                            {[...Array(4)].map((_, j) => (
                                                <div 
                                                    key={`sleeper-${j}`}
                                                    className="sleeper"
                                                    style={{ top: `${j * 25}%` }}
                                                ></div>
                                            ))}
                                        </div>
                                    ))}
                                </div>
                            </div>
                            <div className="cabin-lighting"></div>
                            <div className="window-reflection"></div>
                            <div className={`speed-blur ${speed > 30 ? 'active' : ''}`}></div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Enhanced Control Panel */}
            <div className="control-panel">
                {/* Left Section - Speedometer */}
                <div className="panel-section speedometer">
                    <div className="speedometer-container">
                        <div className="digital-speed">
                            <span className="speed-value">{speed.toFixed(1)}</span>
                            <span className="speed-unit">KM/H</span>
                        </div>
                        <div className="analog-speedometer">
                            <div className="speed-dial">
                                {[...Array(21)].map((_, i) => (
                                    <div 
                                        key={i} 
                                        className={`speed-mark ${i % 2 === 0 ? 'major' : ''}`}
                                        style={{ transform: `rotate(${i * 15 - 150}deg)` }}
                                    >
                                        {i % 2 === 0 && (
                                            <span 
                                                className="speed-number"
                                                style={{ '--rotation': `${-i * 15 + 150}deg` } as any}
                                            >
                                                {i * 10}
                                            </span>
                                        )}
                                    </div>
                                ))}
                                <div className="speed-range speed-range-safe"></div>
                                <div className="speed-range speed-range-warning"></div>
                                <div className="speed-range speed-range-danger"></div>
                                <div 
                                    className={`speed-needle ${Math.abs(speed - prevSpeed) > 1 ? 'changing' : ''}`}
                                    style={{ transform: `rotate(${(speed / 200) * 300 - 150}deg)` }}
                                >
                                    <div className="needle-center"></div>
                                </div>
                                <div className="speed-brand">LOCOMOTIVE</div>
                                <div className="speed-unit-label">KM/H</div>
                                <div className="speedometer-glass"></div>
                            </div>
                        </div>
                    </div>
                    <div className="gauge-container">
                        <div className="brake-gauge">
                            <i className="gauge-icon brake-icon"></i>
                            <div className="gauge-info">
                                <div className="gauge-label">BRAKE PRESSURE</div>
                                <div className="gauge-bar">
                                    <div 
                                        className="gauge-fill brake-fill"
                                        style={{ width: `${brakePressure}%` }}
                                    ></div>
                                </div>
                                <div className="gauge-value">{brakePressure}%</div>
                            </div>
                        </div>
                        <div className="fuel-gauge">
                            <i className="gauge-icon fuel-icon"></i>
                            <div className="gauge-info">
                                <div className="gauge-label">FUEL LEVEL</div>
                                <div className="gauge-bar">
                                    <div 
                                        className="gauge-fill fuel-fill"
                                        style={{ width: `${fuelLevel}%` }}
                                    ></div>
                                </div>
                                <div className="gauge-value">{fuelLevel}%</div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Center Section - Combined Controls and Readouts */}
                <div className="panel-section controls-and-readouts">
                    {/* Top Row - Controls */}
                    <div className="controls-section">
                        <div className="throttle-control">
                            <div className="throttle-label">THROTTLE CONTROL</div>
                            <div className="throttle-section">
                                <div className="throttle-housing">
                                    <div 
                                        className="throttle-slot"
                                        ref={throttleRef}
                                        onMouseDown={handleMouseDown}
                                        onMouseMove={isDragging ? handleMouseMove : undefined}
                                        onMouseUp={handleMouseUp}
                                    >
                                        <div 
                                            className={`throttle-handle ${isDragging ? 'active' : ''} ${engineStatus === 'off' ? 'disabled' : ''}`}
                                            style={{ bottom: `calc(${throttlePosition}% - 25px)` }}
                                        >
                                            <div className="handle-grip">
                                                <div className="grip-lines">
                                                    {[...Array(4)].map((_, i) => (
                                                        <div key={i} className="grip-line" />
                                                    ))}
                                                </div>
                                            </div>
                                            <div className="handle-value">{throttlePosition}%</div>
                                        </div>
                                    </div>
                                    <div className="throttle-scale">
                                        {[...Array(11)].map((_, i) => (
                                            <div key={i} className="scale-mark">
                                                <div className="scale-line" />
                                                <span className="scale-number">{100 - i * 10}</span>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="gauge-cluster">
                            <div className="circular-gauge temperature">
                                <div className="gauge-title">ENGINE TEMP</div>
                                <div className="gauge-face">
                                    <div className="gauge-fill" style={{ transform: `rotate(${(engineTemp / 150) * 180 - 90}deg)` }}></div>
                                    <div className="gauge-center">
                                        <div className="gauge-value">{engineTemp}C</div>
                                    </div>
                                    <div className="gauge-markings">
                                        {[...Array(8)].map((_, i) => (
                                            <div 
                                                key={i} 
                                                className="gauge-mark"
                                                style={{ transform: `rotate(${i * 25 - 90}deg)` }}
                                            />
                                        ))}
                                    </div>
                                </div>
                            </div>

                            <div className="circular-gauge oil-pressure">
                                <div className="gauge-title">OIL PRESSURE</div>
                                <div className="gauge-face">
                                    <div className="gauge-fill" style={{ transform: `rotate(${(oilPressure / 100) * 180 - 90}deg)` }}></div>
                                    <div className="gauge-center">
                                        <div className="gauge-value">{oilPressure} PSI</div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Bottom Row - System Controls and Climate */}
                    <div className="additional-controls">
                        <div className="system-controls">
                            <div className="control-row">
                                <button 
                                    className={`system-btn lights ${lightsOn ? 'active' : ''}`}
                                    onClick={handleLightsToggle}
                                >
                                    <div className="btn-icon"></div>
                                    <span>LIGHTS {lightsOn ? 'ON' : 'OFF'}</span>
                                </button>
                                <button 
                                    className={`system-btn wipers ${wipersOn ? 'active' : ''}`}
                                    onClick={handleWipersToggle}
                                >
                                    <div className="btn-icon"></div>
                                    <span>WIPERS {wipersOn ? 'ON' : 'OFF'}</span>
                                </button>
                            </div>
                            <div className="control-row">
                                <button 
                                    className={`system-btn horn ${hornActive ? 'active' : ''}`}
                                    onMouseDown={handleHornPress}
                                >
                                    <div className="btn-icon"></div>
                                    <span>HORN</span>
                                </button>
                                <button 
                                    className="system-btn emergency"
                                    onClick={handleEmergencyStop}
                                >
                                    <div className="btn-icon"></div>
                                    <span>EMERGENCY</span>
                                </button>
                            </div>
                        </div>
                        <div className="climate-controls">
                            <div className="control-group">
                                <div className="control-label">CABIN TEMP</div>
                                <div className="temp-display">{cabinTemp}C</div>
                                <div className="control-buttons">
                                    <button 
                                        className="temp-btn decrease" 
                                        onClick={() => handleTempChange('down')}
                                    >-</button>
                                    <button 
                                        className="temp-btn increase"
                                        onClick={() => handleTempChange('up')}
                                    >+</button>
                                </div>
                            </div>
                            <div className="control-group">
                                <div className="control-label">FAN SPEED</div>
                                <div className="fan-speed-indicator">
                                    {[...Array(4)].map((_, i) => (
                                        <div key={i} className={`speed-dot ${fanSpeed > i ? 'active' : ''}`} />
                                    ))}
                                </div>
                                <button 
                                    className={`fan-toggle ${fanSpeed > 0 ? 'active' : ''}`}
                                    onClick={handleFanToggle}
                                >
                                    <div className="fan-icon"></div>
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Right Section - Status and Diagnostics */}
            <div className="panel-section status-and-diagnostics">
                <div className="status-lights">
                    <div className={`status-light ${engineStatus === 'on' ? 'active' : ''}`}>
                        <div className="light-housing">
                            <div className="light-indicator"></div>
                            <div className="light-reflection"></div>
                        </div>
                        <span className="light-label">ENGINE</span>
                    </div>
                    <div className={`status-light ${brakePressure > 0 ? 'active' : ''}`}>
                        <div className="light-housing">
                            <div className="light-indicator"></div>
                            <div className="light-reflection"></div>
                        </div>
                        <span className="light-label">BRAKE</span>
                    </div>
                    <div className={`status-light ${fuelLevel < 20 ? 'active' : ''}`}>
                        <div className="light-housing">
                            <div className="light-indicator"></div>
                            <div className="light-reflection"></div>
                        </div>
                        <span className="light-label">FUEL</span>
                    </div>
                </div>
                <div className="console-readouts">
                    <div className="console-group">
                        <div className="console-display system-status">
                            <div className="display-label">SYSTEM STATUS</div>
                            <div className="display-value">OPERATIONAL</div>
                            <div className="status-indicators">
                                <div className="indicator">
                                    <div className="led green"></div>
                                    <span>CPU</span>
                                </div>
                                <div className="indicator">
                                    <div className="led green"></div>
                                    <span>MEM</span>
                                </div>
                                <div className="indicator">
                                    <div className="led yellow"></div>
                                    <span>NET</span>
                                </div>
                            </div>
                        </div>

                        <div className="console-display environmental">
                            <div className="display-label">ENVIRONMENTAL</div>
                            <div className="readings-grid">
                                <div className="reading">
                                    <span className="label">EXT TEMP</span>
                                    <span className="value">24C</span>
                                </div>
                                <div className="reading">
                                    <span className="label">CAB TEMP</span>
                                    <span className="value">21C</span>
                                </div>
                                <div className="reading">
                                    <span className="label">HUMIDITY</span>
                                    <span className="value">65%</span>
                                </div>
                                <div className="reading">
                                    <span className="label">PRESSURE</span>
                                    <span className="value">1013hPa</span>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="console-group">
                        <div className="console-display diagnostics">
                            <div className="display-label">DIAGNOSTICS</div>
                            <div className="diagnostic-list">
                                <div className="diagnostic-item">
                                    <span className="system">Engine Temperature</span>
                                    <span className="value">82C</span>
                                    <div className="status-bar">
                                        <div className="bar-fill" style={{ width: '60%' }}></div>
                                    </div>
                                </div>
                                <div className="diagnostic-item">
                                    <span className="system">Oil Pressure</span>
                                    <span className="value">45 PSI</span>
                                    <div className="status-bar">
                                        <div className="bar-fill" style={{ width: '75%' }}></div>
                                    </div>
                                </div>
                                <div className="diagnostic-item">
                                    <span className="system">Battery</span>
                                    <span className="value">24.5V</span>
                                    <div className="status-bar">
                                        <div className="bar-fill" style={{ width: '90%' }}></div>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="console-display route-info">
                            <div className="display-label">ROUTE INFORMATION</div>
                            <div className="route-details">
                                <div className="detail-row">
                                    <span className="label">NEXT STATION</span>
                                    <span className="value">Central Station</span>
                                </div>
                                <div className="detail-row">
                                    <span className="label">ETA</span>
                                    <span className="value">18:45</span>
                                </div>
                                <div className="detail-row">
                                    <span className="label">DISTANCE</span>
                                    <span className="value">12.4 km</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TrainCabin;
