import * as THREE from 'three';

export function createBasicTrainCabin() {
    const cabin = new THREE.Group();

    // Main cabin body
    const body = new THREE.Mesh(
        new THREE.BoxGeometry(3, 2.5, 4),
        new THREE.MeshPhongMaterial({ color: 0x2c3e50 })
    );
    body.position.y = 1.25;
    cabin.add(body);

    // Windows
    const windowMaterial = new THREE.MeshPhongMaterial({ 
        color: 0x84c1ff,
        transparent: true,
        opacity: 0.6
    });

    // Front window
    const frontWindow = new THREE.Mesh(
        new THREE.PlaneGeometry(2, 1),
        windowMaterial
    );
    frontWindow.position.set(0, 1.5, 2.01);
    cabin.add(frontWindow);

    // Control panel
    const panel = new THREE.Mesh(
        new THREE.BoxGeometry(2, 0.5, 0.1),
        new THREE.MeshPhongMaterial({ color: 0x34495e })
    );
    panel.position.set(0, 1, 1.5);
    cabin.add(panel);

    // Throttle lever
    const lever = new THREE.Mesh(
        new THREE.BoxGeometry(0.1, 0.3, 0.1),
        new THREE.MeshPhongMaterial({ color: 0xe74c3c })
    );
    lever.position.set(0.5, 1.3, 1.5);
    lever.name = "throttle_lever";
    cabin.add(lever);

    return cabin;
}
