import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store';
import { WebSocketService } from '../services/WebSocketService';
import '../styles/TrainCabin.css';

const TrainCabin: React.FC = () => {
    const dispatch = useDispatch();
    const { engineStatus, throttlePosition, speed, brakePressure, fuelLevel } = 
        useSelector((state: RootState) => state.simulation);
    const wsRef = useRef<WebSocketService>();
    const [isDragging, setIsDragging] = useState(false);
    const throttleRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        console.log('Initializing WebSocket service...');
        wsRef.current = new WebSocketService(dispatch);
        wsRef.current.connect();

        return () => {
            if (wsRef.current) {
                wsRef.current.disconnect();
            }
        };
    }, [dispatch]);

    const handleEngineToggle = () => {
        if (!wsRef.current) return;
        const newStatus = engineStatus === 'off' ? 'on' : 'off';
        wsRef.current.sendUpdate({
            engine_status: newStatus,
            throttle_position: newStatus === 'off' ? 0 : throttlePosition
        });
    };

    const handleThrottleMove = useCallback((clientY: number) => {
        if (!throttleRef.current || engineStatus === 'off') return;

        const rect = throttleRef.current.getBoundingClientRect();
        const height = rect.height;
        const relativeY = clientY - rect.top;
        const percentage = Math.max(0, Math.min(100, (1 - relativeY / height) * 100));

        wsRef.current?.sendUpdate({ throttle_position: Math.round(percentage) });
    }, [engineStatus]);

    const handleMouseDown = (e: React.MouseEvent) => {
        if (engineStatus === 'off') return;
        setIsDragging(true);
        handleThrottleMove(e.clientY);
    };

    const handleMouseMove = (e: React.MouseEvent) => {
        if (!isDragging) return;
        handleThrottleMove(e.clientY);
    };

    const handleMouseUp = () => {
        setIsDragging(false);
    };

    useEffect(() => {
        if (isDragging) {
            document.addEventListener('mouseup', handleMouseUp);
            document.addEventListener('mousemove', handleMouseMove as any);
        }
        return () => {
            document.removeEventListener('mouseup', handleMouseUp);
            document.removeEventListener('mousemove', handleMouseMove as any);
        };
    }, [isDragging, handleMouseMove]);

    return (
        <div className="train-cabin">
            <div className="cabin-interior">
                {/* Side Windows */}
                <div className="side-windows">
                    <div className="window left"></div>
                    <div className="window right"></div>
                </div>

                {/* Main Window */}
                <div className="main-window">
                    <div className="window-frame">
                        <div className="window-view">
                            <div className="sky"></div>
                            <div className="ground"></div>
                            <div className="track-perspective">
                                <div className="rail-bed"></div>
                                <div className="rail left"></div>
                                <div className="rail right"></div>
                                {[...Array(20)].map((_, i) => (
                                    <div 
                                        key={i} 
                                        className="sleeper"
                                        style={{ 
                                            bottom: `${i * 5}%`,
                                            transform: `translateZ(${i * 50}px)` 
                                        }}
                                    />
                                ))}
                            </div>
                        </div>
                    </div>
                    <div className="window-frame-overlay"></div>
                </div>

                {/* Control Console */}
                <div className="control-console">
                    <div className="console-top">
                        <div className="console-panel left">
                            <div className="speedometer-housing">
                                <div className="speedometer-container">
                                    <div className="speedometer-bezel">
                                        <div className="speedometer">
                                            {[...Array(12)].map((_, i) => (
                                                <div 
                                                    key={i} 
                                                    className="speed-mark"
                                                    style={{ transform: `rotate(${i * 30}deg)` }}
                                                >
                                                    <span className="speed-number">
                                                        {i * 20}
                                                    </span>
                                                </div>
                                            ))}
                                            <div 
                                                className="speed-needle"
                                                style={{ transform: `rotate(${(speed / 200) * 270 - 135}deg)` }}
                                            />
                                            <div className="speed-center">
                                                <div className="digital-speed">{speed.toFixed(1)}</div>
                                                <div className="speed-unit">km/h</div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="gauge-cluster">
                                <div className="gauge brake-gauge">
                                    <div className="gauge-label">BRAKE PRESSURE</div>
                                    <div className="gauge-value">{brakePressure}%</div>
                                    <div className="gauge-bar">
                                        <div 
                                            className="gauge-fill brake"
                                            style={{ width: `${brakePressure}%` }}
                                        />
                                    </div>
                                </div>
                                <div className="gauge fuel-gauge">
                                    <div className="gauge-label">FUEL LEVEL</div>
                                    <div className="gauge-value">{fuelLevel}%</div>
                                    <div className="gauge-bar">
                                        <div 
                                            className="gauge-fill fuel"
                                            style={{ width: `${fuelLevel}%` }}
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="console-panel center">
                            <div className="engine-controls">
                                <div className="status-panel">
                                    <div className={`status-display ${engineStatus}`}>
                                        <div className="status-light"></div>
                                        <div className="status-text">
                                            ENGINE: {engineStatus.toUpperCase()}
                                        </div>
                                    </div>
                                </div>
                                <div className="control-buttons">
                                    <button
                                        className={`engine-button ${engineStatus}`}
                                        onClick={handleEngineToggle}
                                    >
                                        {engineStatus === 'off' ? 'START ENGINE' : 'STOP ENGINE'}
                                    </button>
                                </div>
                            </div>
                        </div>

                        <div className="console-panel right">
                            <div className="warning-cluster">
                                <div className={`warning-light ${engineStatus === 'on' ? 'active' : ''}`}>
                                    <div className="light-indicator"></div>
                                    <span>ENGINE</span>
                                </div>
                                <div className={`warning-light ${brakePressure < 30 ? 'active' : ''}`}>
                                    <div className="light-indicator"></div>
                                    <span>BRAKE</span>
                                </div>
                                <div className={`warning-light ${fuelLevel < 20 ? 'active' : ''}`}>
                                    <div className="light-indicator"></div>
                                    <span>FUEL</span>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="console-bottom">
                        <div className="throttle-section">
                            <div className="throttle-label">THROTTLE</div>
                            <div 
                                className="throttle-housing"
                                ref={throttleRef}
                                onMouseDown={handleMouseDown}
                                onMouseMove={handleMouseMove}
                                onMouseUp={handleMouseUp}
                            >
                                <div className="throttle-scale">
                                    {[...Array(11)].map((_, i) => (
                                        <div key={i} className="scale-mark">
                                            <span className="mark-label">{100 - i * 10}</span>
                                        </div>
                                    ))}
                                </div>
                                <div className="throttle-slot">
                                    <div 
                                        className={`throttle-handle ${isDragging ? 'active' : ''} ${engineStatus === 'off' ? 'disabled' : ''}`}
                                        style={{ bottom: `${throttlePosition}%` }}
                                    >
                                        <div className="handle-grip"></div>
                                        <div className="handle-value">{throttlePosition}%</div>
                                    </div>
                                </div>
                                <div className="throttle-track">
                                    <div className="track-fill" style={{ height: `${throttlePosition}%` }}></div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TrainCabin;
